function getmessage(){
var pagenum = parseInt($('#messagelist').attr('pagenum'))+1;
var totalpage = $('#messagelist').attr('totalpage');
$('.mymsg').show();
if(pagenum>totalpage){
$('#moremsg').html('没有更多消息');
return;
}
$.ajax({
url: hs_const_url.bookajax,
type: 'post',
dataType: 'json',
async: false,
data: {
method: 'getUserMessage',
pagenum: pagenum
},
success: function(data) {
if(data.status==1){
$('#moremsg').html('正在加载..');
setTimeout(function(){
var html = template('messagetpl',data);
$('#messagelist').append(html).attr({pagenum:data.pagenum,totalpage:data.totalpage});
$('#moremsg').html('加载更多');
},1000)
}else{
$('#moremsg').html('暂无消息');
}

}
})
}
function deletemsg(){
$.ajax({
url: hs_const_url.bookajax,
type: 'get',
dataType: 'jsonp',
async: false,
data: {
method: 'readUserMessage'
},
success: function(data) {
if(data.status==1){
$('#messagelist').html('');
$('#moremsg').html('暂无消息');
}
}
})
}
