function getbookstatus() {
var url = "https://www.hongshu.com"+"/"+"bookajax.do";
var bid = '42203';
$.ajax({
type:'get',
url: url,
data: {bid: bid,method:"getbookstatus"},
success: function (data) {
console.log(data);
if(data.status == 1){
if (data['list']['isxiajia']) {
$('.qh').attr("href","https://www.hongshu.com/xiajia.html");
$('.progress').hide();
}
}
},
});
}
