BODY.SCRIPT:
$(function () {
getWorks();
getbookstatus();
});
function getWorks() {
var url = "/chahua";
$.ajax({
dataType: 'jsonp',
url: url,
data: {a:'getWorklists', neednum: 5, bid: '42203', type: 'reward'},
success: function (data) {
if (data.status == 1) {
var html = template('worksTpl', data);
$('#workslist').html(html);
}
},
});
}

function getbookstatus() {
var url = "https://www.hongshu.com"+"/"+"bookajax.do";
$.ajax({
type:'get',
url: url,
data: {bid:'42203',method:"getbookstatus"},
success: function (data) {
if(data.status == 1){
if (data['list']['isxiajia']) {
$('.zanshang').hide();
$('#divleftMenu').hide();
$('#divrightPager').hide();
}
}
},
});
}

(function(){
var hs_ga = document.createElement('script');
hs_ga.type = 'text/javascript';
hs_ga.async = true;
hs_ga.src = hs_const_url.tjsitecount_js;
var s = document.getElementsByTagName('script')[0];
s.parentNode.insertBefore(hs_ga, s);
})();
$(function () {
// 获取链接中的sid
var urlhash =  document.location.hash;
var _bid = '';
if (!_bid) {
_bid = '42203';
}
var _chapterid = '';
if (!_chapterid) {
_chapterid = '';
}
if (!_chapterid) {
_chapterid = '1378791';
}
// 获取cookie中sid
var sid = CookieUtil.getCookie('_from_sid');
if (urlhash) {
var fromsid = '';
if (sid) {
fromsid = sid;
} else {
fromsid = urlhash.substring(urlhash.indexOf('fromsid=')+8,urlhash.length);
}
if(typeof fromsid !== undefined && fromsid != '' && fromsid != null){
if(!sid){
sid = fromsid;
}
}
}

if (sid) {
sendsid(sid,_bid,_chapterid);
var img=new Image();
img.setAttribute('lazy','y');
var url = "/home/tjsitecount/setsid.html" + '?sid=' + sid;
img.src = url;
$('body').append(img);

}
});
function sendsid(fromsid, bid, chapid) {

if(fromsid!=undefined && fromsid!='' && fromsid!=null) {
var oImg = new Image();
oImg.setAttribute('lazy','y');
var url = "/home/tjsitecount/statics.html" + '?sid=' + fromsid + '&bid='+bid + '&cid=' + chapid;
oImg.src = url;
$('body').append(oImg);
}

}
